import React, { useState } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import logo from "../logo.webp";
import R from "../R.png";

const Nav = () => {
  const navigate = useNavigate();
  const [isLoggedin, setIsLoggedin] = useState(true);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const username = "John Doe";
  const dropdownRef = useRef(null); // Ref for the dropdown

  const logout = () => {
    localStorage.removeItem("token-info");
    setIsLoggedin(false);
    navigate("/");
  };
  useEffect(() => {
    const handleOutsideClick = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    };

    document.addEventListener("mousedown", handleOutsideClick);
    return () => {
      document.removeEventListener("mousedown", handleOutsideClick);
    };
  }, []);

  return (
    <nav className="bg-white justify-center">
      <div className="max-w-7xl mx-auto px-6 lg:px-8">
        <div className="flex  h-16">
          <Link to="/" className="flex ">
            <img src={logo} alt="Logo" className="h-14 w-500 mr-3" />
          </Link>

          <div className="flex space-x-4 mx-8 items-center justify-center">
            <NavLink
              to="/"
              style={({ isActive }) => ({
                backgroundColor: isActive ? "#E4655C" : "white",
                color: isActive ? "white" : "#E4655C",
              })}
              className="text-purple-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-large"
            >
              Home
            </NavLink>
            <NavLink
              to="/Cetegories"
              style={({ isActive }) => ({
                backgroundColor: isActive ? "#E4655C" : "white",
                color: isActive ? "white" : "#E4655C",
              })}
              className="text-gray-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-medium"
            >
              Category
            </NavLink>
            <NavLink
              to="/Recipes"
              style={({ isActive }) => ({
                backgroundColor: isActive ? "#E4655C" : "white",
                color: isActive ? "white" : "#E4655C",
              })}
              className="text-gray-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-medium"
            >
              Recipes
            </NavLink>
            <NavLink
              to="/AboutUs"
              style={({ isActive }) => ({
                backgroundColor: isActive ? "#E4655C" : "white",
                color: isActive ? "white" : "#E4655C",
              })}
              className="text-gray-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-medium"
            >
              About Us
            </NavLink>
          </div>

          {/* User Icon and Dropdown */}
          <div
            className="relative flex items-center ml-auto text-#E4655C "
            ref={dropdownRef}
          >
            <img src={R} alt="Logo" className="h-10 w-55 mr-3" />
            <i
              className="fas fa-user-circle mr-2 cursor-pointer"
              onClick={() => setDropdownOpen((prev) => !prev)} // Toggle dropdown
            />
            <span>{username}</span>

            {dropdownOpen && (
              <div className="absolute right-0 mt-2 w-48 bg-gray-900 text-white rounded-md shadow-lg z-10">
                <div className="py-2">
                  <button
                    onClick={logout}
                    className="block px-4 py-2 text-sm hover:bg-gray-700 w-full text-left"
                  >
                    Logout
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Nav;
