import React, { useState } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import logo from "../logo.webp";

const Nav = () => {
  const navigate = useNavigate();
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const username = "John Doe"; // Placeholder for the username (you can replace this with dynamic data)

  const logout = () => {
    localStorage.removeItem("token-info");
    setDropdownOpen(false); // Close dropdown on logout
    navigate("/"); // Redirect after logout
  };

  return (
    <nav className="bg-gray-800">
      <div className="max-w-7xl mx-auto px-6 lg:px-8">
        <div className="flex items-center h-16">
          {/* Logo Section */}
          <Link to="/" className="flex items-center">
            <img
              src={logo} // Replace with the actual path to your logo
              alt="Logo"
              className="h-8 w-8 mr-3" // Adjust the size of the logo as necessary
            />
          </Link>

          <div className="flex space-x-4 mx-8 items-center justify-center">
            <NavLink
              to="/"
              exact
              className={({ isActive }) =>
                isActive
                  ? "text-yellow-500 px-3 py-2 rounded-md text-sm font-medium"
                  : "text-gray-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-medium"
              }
            >
              Home
            </NavLink>
            <NavLink
              to="/category"
              className={({ isActive }) =>
                isActive
                  ? "text-yellow-500 px-3 py-2 rounded-md text-sm font-medium"
                  : "text-gray-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-medium"
              }
            >
              Category
            </NavLink>
            <NavLink
              to="/about"
              className={({ isActive }) =>
                isActive
                  ? "text-yellow-500 px-3 py-2 rounded-md text-sm font-medium"
                  : "text-gray-300 hover:bg-gray-700 hover:text-white px-3 py-2 rounded-md text-sm font-medium"
              }
            >
              About Us
            </NavLink>
          </div>

          {/* User Icon and Dropdown */}
          <div className="relative ml-auto flex items-center text-white">
            <i
              className="fas fa-user-circle text-xl cursor-pointer"
              onClick={() => setDropdownOpen(!dropdownOpen)}
            />
            <span className="ml-2">{username}</span>
            {dropdownOpen && (
              <div className="absolute right-0 mt-2 w-48 bg-gray-900 text-white rounded-md shadow-lg z-10">
                <div className="py-2">
                  <button
                    onClick={logout}
                    className="block w-full text-left px-4 py-2 text-sm hover:bg-gray-700"
                  >
                    Logout
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

